import { Route, Routes } from "react-router-dom";
import React, { Component } from "react";
import Appbar from "./components/Appbar";
import "./App.css";
import Homepage from "./components/Homepage";
import Cart from "./components/Cart";
import FruitsPage from "./pages/FruitsPage";
import VeggiesPage from "./pages/VeggiesPage";
import SpecialPage from "./pages/SpecialPage";
import Layout from "./components/layout/Layout";

fatch
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
  }

  callAPI() {
    fetch("http://localhost:9000/testAPI")
      .then((res) => res.text())
      .then((res) => this.setState({ apiResponse: res }));
  }
  componentWillUnmount() {
    this.callAPI();
  }

  //function App() {
  render() {
    return (
      <Layout>
        <main>
          <Routes>
            <Route path="/" element={<Homepage />} />
            <Route path="/products/fruits" element={<FruitsPage />} />
            <Route path="/products/veggies" element={<VeggiesPage />} />
            <Route path="/products/special" element={<SpecialPage />} />
            <Route path="/fruits" element={<FruitsPage />} />

            {/* <Route path='/product' exact element= {<Cart />} /> */}
            <Route path="/cart" element={<Cart />} />
          </Routes>
        </main>
      </Layout>

      // <Route path='/'>
      //   <Homepage />
      // </Route>

      // <Route path='/cart'>
      //   <Cart />
      // </Route>
      // <Route path='/'>
      //   <Homepage />
      // </Route>
    );
  }
}

/* <div>
<Appbar />
<Homepage />
<Cart />
  </div> */
export default App;
